jQuery(document).ready(function(){INVESTMENT_STORAGE.media_frame=null;INVESTMENT_STORAGE.media_link="";jQuery(".investment_media_selector").on("click",function(a){investment_show_media_manager(this);a.preventDefault();return false})});function investment_show_media_manager(a){INVESTMENT_STORAGE.media_link=jQuery(a);if(INVESTMENT_STORAGE.media_frame){INVESTMENT_STORAGE.media_frame.open();return false}INVESTMENT_STORAGE.media_frame=wp.media({title:INVESTMENT_STORAGE.media_link.data("choose"),library:{type:"image"},multiple:INVESTMENT_STORAGE.media_link.data("multiple")===true?"add":false,button:{text:INVESTMENT_STORAGE.media_link.data("update"),close:true}});INVESTMENT_STORAGE.media_frame.on("select",function(b){var c=jQuery("#"+INVESTMENT_STORAGE.media_link.data("linked-field")).eq(0);var e="";if(INVESTMENT_STORAGE.media_link.data("multiple")===true){INVESTMENT_STORAGE.media_frame.state().get("selection").map(function(f){e+=(e?"\n":"")+f.toJSON().url});var d=c.val();e=d+(d?"\n":"")+e}else{e=INVESTMENT_STORAGE.media_frame.state().get("selection").first().toJSON().url}c.val(e);if(c.siblings("img").length>0){c.siblings("img").attr("src",e)}c.trigger("change")});INVESTMENT_STORAGE.media_frame.open();return false};